function fade(type, elem, ms, prop) {
    let  isIn = type === 'in'
        let opacity = isIn ? 0 : 1
        let interval = 50
        let duration = ms
        let gap = interval / duration
        let self = elem;
    
      if(isIn) {
        if (prop === undefined || prop === null) {
            self.style.display = 'block';
        } else {
            self.style.display = prop
        }

        self.style.opacity = opacity;
      }
    
      function func() {
        opacity = isIn ? opacity + gap : opacity - gap;
        self.style.opacity = opacity;
    
        if(opacity <= 0) self.style.display = 'none'
        if(opacity <= 0 || opacity >= 1) window.clearInterval(fading);
      }
    
      var fading = window.setInterval(func, interval);
}

class jQueryElement {
    
    constructor(element) {
        this.elem = element;
    }
    // hide function
    hide() {
        this.elem.style.display = 'none'
    }
    // show function
    show(displayProp) {
        let dis = 'block'
        if (displayProp !== undefined && displayProp !== null ){
            dis = displayProp
        }
        this.elem.style.display = dis;
        this.elem.style.visibility = 'visible';
    }
    css(prop, value) {
        let good = cssTable[prop]
        console.log(good, value)
        this.elem.style[good] = value;
    }

    fadeIn(ms, prop) {
        fade('in', this.elem, ms, prop)
    }
    fadeOut(ms, prop) {
        fade("out", this.elem, ms, prop)
    }
    html(markup) {
        if (markup === undefined || markup === null) {
            return this.elem.innerHtml;
        }
        this.elem.innerHtml = markup;
    }
    attr(name, value) {
        this.elem.setAttribute(name, value);
    }

}



// main $ function
const $ = (selector) => {
    return new jQueryElement(document.querySelector(selector))
}
